import React, { useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { saveShippingAddress } from '../actions/cartActions';
import CheckoutSteps from '../components/CheckoutSteps';

function ShippingAddressScreen(props){
    const userSignin = useSelector(state => state.userSignin);
    const {userInfo} = userSignin;
    if (!userInfo) {
        props.history.push('/signin'); 
    }
    const cart = useSelector(state => state.cart); //from store.js
    const {shippingAddress} = cart; //click back will show previously filled info
    const [fullName, setFullName] = useState(shippingAddress.fullName);
    const [address, setAddress] = useState(shippingAddress.address);
    const [city, setCity] = useState(shippingAddress.city);
    const [postalCode, setPostalCode] = useState(shippingAddress.postalCode);
    const [country, setCountry] = useState(shippingAddress.country);
    const dispatch = useDispatch();
    const submitHandler = e => { //dispatch save shipping address action
        e.preventDefault();
        dispatch(saveShippingAddress({fullName, address, city, postalCode, country})); //connect
        props.history.push('/payment'); //to payment screen
    }
    const chooseOnMap = () => {
        dispatch(saveShippingAddress({fullName, address, city, postalCode, country,}));
        props.history.push('/payment');
    }
    return(
        <div>
            <CheckoutSteps step1 step2 />
            <form className="form" onSubmit={submitHandler}>
                <div>
                    <h1>Shipping Address</h1>
                </div>
                <div>
                    <label htmlFor="fullName">Full Name</label>
                    <input type="text" id="fullName" placeholder="Enter first name + last name" value={fullName} onChange={e => setFullName(e.target.value)} required />
                </div>
                <div>
                    <label htmlFor="address">Address</label>
                    <input type="text" id="address" placeholder="Enter address" value={address} onChange={e => setAddress(e.target.value)} required />
                </div>
                <div>
                    <label htmlFor="city">City</label>
                    <input type="text" id="city" placeholder="Enter city" value={city} onChange={e => setCity(e.target.value)} required />
                </div>
                <div>
                    <label htmlFor="postalCode">Postal Code</label>
                    <input type="text" id="postalCode" placeholder="Enter postal code" value={postalCode} onChange={e => setPostalCode(e.target.value)} required />
                </div>
                <div>
                    <label htmlFor="country">Country</label>
                    <input type="text" id="country" placeholder="Enter country" value={country} onChange={e => setCountry(e.target.value)} required />
                </div>
                <div>
                    <label htmlFor="chooseOnMap">Location</label>
                    <button type="button" onClick={chooseOnMap}>Choose On Map</button>
                </div>
                <div>
                    <label />
                    <button className="primary" type="submit">Continue</button>
                </div>
            </form>
        </div>
    )
}

export default ShippingAddressScreen;
